mapper.select:
select RGROUP_ID,RGROUP_NAME,USER_ID,USERNAME ,A.STATUS,PASSWORD,ORG_CODE ,TS.CHINESE_NAME ,TS.ENGLISH_NAME FROM ( SELECT G.RGROUP_ID,G.RGROUP_NAME,U.USER_ID,U.USERNAME ,U.STATUS,U.PASSWORD,U.ORG_CODE FROM SET_ROLE_GROUP G , SET_USER U, SET_USER_RGROUP UR WHERE G.RGROUP_ID = UR.RGROUP_ID AND U.USER_ID = UR.USER_ID
AND U.USERNAME LIKE '%'||#{params.userName}||'%'
) A left join T_SUPPLIER TS on A.ORG_CODE = TS.SUPPLIER_ID ORDER BY USER_ID DESC
