mapper.select:
select A.Project_ID,A.Longitude,A.Latitude,A.community_Name,A.Province_Id, A.City_Code, A.District_Code, Building_NO,Room_Number,layout,renovation,B.T_House_ID,B.LONG_PRICE,b.house_id from t_project A, T_HOUSE B WHERE A.PROJECT_ID = B.PROJECT_ID
AND A.City_Code = #{cityCode}
AND B.Status = '1'
AND B.LONG_PRICE <= #{longPrice}
AND B.Rental_Type = #{rentalType}
order by A.Project_ID , B.AREA,A.Total_Floor DESC,B.Long_Price ASC

SELECT
th.House_ID,
tp.Latitude,
tp.Longitude
FROM
t_house th,
t_project tp
WHERE
th.project_id = tp.project_id
AND th.Status = '1'

    and th.Long_Price  <= #{longPrice}


    and th.Long_Price  >= #{longPrice}





        AND th.layout = #{layout}


        AND th.layout = #{layout}


        AND th.layout >= #{layout}

SELECT t1.Project_ID, t1.House_ID, t1.Long_Price, t1.Community_Name, t1.Use_Area, t1.layout, t1.Doorplate, t1.Building_NO, t1.Room_Number, t1.Hall_Quantity, t1.Longitude, t1.Latitude, t2.picture FROM ( SELECT th.Project_ID, th.House_ID, th.Long_Price, th.Community_Name, th.Use_Area, th.layout, th.Doorplate, th.Building_NO, th.Room_Number, th.Hall_Quantity, tp.Longitude, tp.Latitude FROM t_house th, t_project tp WHERE th.Project_ID = tp.Project_ID AND th.House_ID IN
#{houseId}
) t1 LEFT JOIN ( SELECT house_id, min(picture) AS picture FROM t_house_pic WHERE House_ID IN
#{houseId}
GROUP BY House_ID ) t2 ON t1.House_ID = t2.house_id

select x.*,y.picture from (
select
A.Project_ID,A.Longitude,A.Latitude,B.House_ID,B.LONG_PRICE,b.COMMUNITY_NAME,b.DOORPLATE,b.BUILDING_NO,b.ROOM_NUMBER
, b.USE_AREA,b.LAYOUT,b.HALL_QUANTITY
from t_project A, (select * from T_HOUSE bb where bb.Status = '1'

    and bb.LONG_PRICE  <=
    #{longPrice}


    and bb.LONG_PRICE  >=
    #{longPrice}





        AND bb.layout = #{layout}


        AND bb.layout = #{layout}


        AND bb.layout >= #{layout}


) B WHERE A.PROJECT_ID = B.PROJECT_ID
order by B.Long_Price DESC limit

    #{stackIndex}

0
,10
) X join (select * from t_house_pic C ) as Y on
x.house_id=Y.house_id group by x.house_id
order by x.Long_Price DESC

select x.*,y.picture from(
select
A.Project_ID,A.Longitude,A.Latitude,B.House_ID,B.LONG_PRICE,b.COMMUNITY_NAME,b.DOORPLATE,b.BUILDING_NO,b.ROOM_NUMBER
, b.USE_AREA,b.LAYOUT,b.HALL_QUANTITY
from t_project A, (select * from T_HOUSE bb where bb.Status = '1'

    and bb.LONG_PRICE   <=
    #{longPrice}





        AND bb.layout = #{layout}


        AND bb.layout = #{layout}



            AND bb.layout >= #{layout}



) B
WHERE A.PROJECT_ID = B.PROJECT_ID

and b.COMMUNITY_NAME=#{communityName}
order by B.Long_Price DESC limit

    #{stackIndex}

